package code;

public class Demo2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
		 * 0b100
		 * 0100
		 * 100
		 * 0x100
		 */
		System.out.println(0b100);   //  二进制表示形式前面加0b
		System.out.println(0100);    //  八进制表示形式前面加0
		System.out.println(100);     //  十进制
		System.out.println(0x100);   //  十六进制表示形式前面加0x（可以大写也可以小写）
	}

}
