package code;

public class Demo5_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//赋值运算符
		int a = 3;  //把右边的常量值赋值给左边的变量,左边必须是变量
		a += 4;     // a = a + 4
		System.out.println(a);
		
		//关系运算符 ==，！=，>,>=,<,<=
		System.out.println(4 == 3);
		System.out.println(4 != 3);
		System.out.println(4 > 3);
		System.out.println(4 >= 3);
		System.out.println(4 < 3);
		System.out.println(4 <= 3);

	}

}
