package code2;

public class Day2_Code1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
    		位运算符的基本用法1(二进制操作）
    		&,|,^,~ 的用法
    		&:有0则0
    		|:有1则1
    		^:相同则0，不同则1
    		~:按位取反
		 */
		System.out.println(6 & 3);
		System.out.println(6 | 3);
		System.out.println(6 ^ 3);
		System.out.println(~6);

	}

}
