package code2;

public class Day2_Code4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		/*
    		A:三元运算符的格式
    		(关系表达式) ? 表达式1 : 表达式2;
    		B:三元运算符的执行流程
    		C:案例演示
        		获取两个数中的最大值
		 */
		int x = 10;
		int y = 5;
		int z;
		z = (x > y) ? x : y;
		System.out.println("z = " + z);

	}

}
