package code9;

public class Demo5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// 位运算符
		int a = 3;
		int b = 4;
		System.out.println(a&b);
		System.out.println(a|b);
		System.out.println(a^b);
		System.out.println(~a); // 取反
		
		//移位
		int c = 3 << 2;
		System.out.println(c);
		

	}

}
